var rs = require('readline-sync');
var numberOne = 0;
var numberTwo = 0;
const operators = ['/', '*', '-', '+'];

function division(num1, num2) {
  return total = (num1 / num2);
}
function multiplication(num1, num2) {
  return total = (num1 * num2);
}
function subtraction(num1, num2) {
  return total = (num1 - num2);
}
function addition(num1, num2) {
  return total = (Number(num1) + Number(num2));
}

mathOperator = rs.question('Select the operation ( / , * , - , or + ).  ');

while (!operators.includes(mathOperator)) {
  console.log('That is not a valid operation');
  mathOperator = rs.question('Select the operation ( / , * , - , or + ).  ');
}

numberOne = rs.question('Pick your first number!  ');

while (isNaN(numberOne)) {
  numberOne = rs.question('That wasn\'t a number. Please pick your first number!  ');
}

numberTwo = rs.question('Pick your second number!  ');

while (isNaN(numberTwo)) {
  numberTwo = rs.question('That wasn\'t a number. Please pick your second number!  ');
}

if (mathOperator == operators[0]) { division(numberOne, numberTwo)};
if (mathOperator == operators[1]) { multiplication(numberOne, numberTwo)};
if (mathOperator == operators[2]) { subtraction(numberOne, numberTwo)};
if (mathOperator == operators[3]) { addition(numberOne, numberTwo)};




