var rs = require('readline-sync');
var numberOne = 0;
var numberTwo = 0;
const operators = ['/', '*', '-', '+'];

function makeCalculation (operation, num1, num2) {
  if (operation === '+') {
      return num1 + num2;
  } else if (operation === '-') {
      return num1 - num2;
  } else if (operation === '*') {
      return num1 * num2;
  } else {
      return num1 / num2;
  }
}

mathOperator = rs.question('Select the operation ( / , * , - , or + ).  ');
  while (!operators.includes(mathOperator)) {
    console.log('That is not a valid operation');
    mathOperator = rs.question('Select the operation ( / , * , - , or + ).  ');
  }

numberOne = rs.questionInt('Pick your first number!  ', {
  limitMessage: 'This is not a number'
});

numberTwo = rs.questionInt('Pick your second number!  ', {
  limitMessage: 'This is not a number'
});

console.log(makeCalculation (mathOperator, numberOne, numberTwo));
